import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import SetTime from "../pomodoro/SetTime";
import ActiveSession from "../pomodoro/ActiveSession"



function nextTick(prevState) {
  const timeRemaining = Math.max(0, prevState.timeRemaining - 1);
  return {
    ...prevState,
    timeRemaining,
  };
}

function nextSession(focusDuration, breakDuration) {
  return (currentSession) => {
    if (currentSession.label === "Focusing") {
      return {
        label: "On Break",
        duration: breakDuration,
        timeRemaining: breakDuration * 60,
      };
    }
    return {
      label: "Focusing",
      duration: focusDuration,
      timeRemaining: focusDuration * 60,
    };
  };
}



function Pomodoro() {
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [session, setSession] = useState(null);
  const [valueBar, setValueBar] = useState(0)
  const [focusDuration, setFocusDuration] = useState(25);
  const [breakDuration, setBreakDuration] = useState(5);
  const [buttonDisabled, setButtonDisabled] = useState(true);
  const [buttonEnabled, setButtonEnabled] = useState(false);



  function stopSession() {
    if (session != null) {
      setSession(null);
      setIsTimerRunning(false);
      setButtonDisabled(true);
      setButtonEnabled(false);
    }
    return session
  }


  useInterval(() => {
      let progress = (((session.duration*60)-session.timeRemaining)/(session.duration*60)) * 100;
      setValueBar(progress);
      if (session.timeRemaining === 0) {
        new Audio("https://bigsoundbank.com/UPLOAD/mp3/1482.mp3").play();
        return setSession(nextSession(focusDuration, breakDuration));
      }
      return setSession(nextTick);
    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setButtonDisabled(false);
    setButtonEnabled(true);
    setIsTimerRunning((prevState) => {
      const nextState = !prevState;
      if (nextState) {
        setSession((prevStateSession) => {
          if (prevStateSession === null) {
            return {
              label: "Focusing",
              duration: focusDuration,
              timeRemaining: focusDuration * 60,
            };
          }
          return prevStateSession;
        });
      }
      return nextState;
    });
  }

  return (
    <div className="pomodoro">
      <SetTime focusDuration={focusDuration} 
      breakDuration={breakDuration} 
      setBreakDuration={setBreakDuration} 
      setFocusDuration={setFocusDuration} 
      buttonEnabled={buttonEnabled}/>
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !isTimerRunning,
                  "oi-media-pause": isTimerRunning,
                })}
              />
            </button>
            <button
              type="button"
              className="btn btn-secondary"
              id="stop-button"
              data-testid="stop"
              title="Stop the session"
              disabled={buttonDisabled}
              onClick={stopSession}
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <ActiveSession buttonDisabled={buttonDisabled} valueBar={valueBar} session={session}/>
    </div>
  );
}

export default Pomodoro;
